        END;

        IF @Intake_End_Date <= @Intake_Start_Date
        BEGIN
            RAISERROR('CK Error: Intake_End_Date must be after Intake_Start_Date.', 16, 1);
            ROLLBACK TRANSACTION;
            RETURN -1;
