        -- 3. Perform INSERT
        INSERT INTO Intake (Intake_ID, Intake_Name, Intake_Type, Intake_Start_Date, Intake_End_Date)
        VALUES (@Intake_ID, @Intake_Name, @Intake_Type, @Intake_Start_Date, @Intake_End_Date);

