        BEGIN
            -- Select all records
            SELECT * FROM Rating;
        END
    END TRY
    BEGIN CATCH
        DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
        RAISERROR('Error selecting Rating: %s', 16, 1, @ErrorMessage);
        RETURN -1;
    END CATCH
END;
